// change-password.page.ts
import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { UserService } from 'src/services/UserService/user-service.service';

@Component({
  selector: 'app-change-password',
  templateUrl: './change-password.page.html',
  styleUrls: ['./change-password.page.scss'],
})


export class ChangePasswordPage {
  email: string | null;


  constructor(private route: ActivatedRoute, private userService: UserService) {
    this.email = this.route.snapshot.paramMap.get('email');
  }

  changePassword(newPassword: string) {
    this.userService.updateUserPassword(this.email!, newPassword);
  }

}
<!-- change-password.page.html -->
<ion-header>
  <ion-toolbar>
    <ion-title>Cambiar Contraseña</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-item>
    <ion-label>Email actual:</ion-label>
    <ion-input [value]="email" readonly></ion-input>
  </ion-item>

  <ion-item>
    <ion-label>Nueva Contraseña:</ion-label>
    <ion-input type="password" [(ngModel)]="newPassword"></ion-input>
  </ion-item>

  <ion-button (click)="changePassword(newPassword)">Cambiar Contraseña</ion-button>
</ion-content>
