import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';


@Component({
  selector: 'app-menu',
  templateUrl: 'menu.page.html',
  styleUrls: ['menu.page.scss'],
})

// Declaraciones para las opciones del menu //
export class MenuPage implements OnInit {


  constructor(
    private router: Router,
  ) {}

  //Obtencion de Nombre de usuario para mostrar en el mensaje de bienvenida //
  ngOnInit() {
  }


  // Funciones de navegacion de botones //

  marcarAsistencia(){
    this.router.navigate(['/asignaturas']);
  }


}

<app-main-menu></app-main-menu>

<ion-content id="Body">
  <div id="container">
  <ion-item id="Case-Button">
    <ion-item id="item-Report">
      <ion-button id="Report">Resumen de Asistencia</ion-button>
    </ion-item>
    <ion-item id="item-Mark">
      <ion-button id="Mark" (click)="marcarAsistencia()">Marcar Asistencia</ion-button>
    </ion-item>
  </ion-item>
</div>
</ion-content>
