import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { MenuController } from '@ionic/angular';
import { SharedService } from '../../services/shared.service';
import { ServiceGuardService } from 'src/services/loginGuard/service-guard.service';

interface MenuItem {
  label: string;
  icon: string;
  destination: string;
}

@Component({
  selector: 'app-main-menu',
  templateUrl: './main-menu.component.html',
  styleUrls: ['./main-menu.component.scss'],
})

export class MainMenuComponent implements OnInit {
  username: string | null = null;
  menuItems: MenuItem[] = [
    { label: 'Inicio', icon: 'Home', destination: 'menu' },
    { label: 'Cuenta', icon: 'person', destination: 'cuenta' },
    { label: 'Perfil', icon: 'person-circle', destination: 'perfil' },
    { label: 'Asignaturas', icon: 'book', destination: 'asignaturas' },
    { label: 'Cerrar Sesión', icon: 'exit', destination: 'Cerrar Sesion' }
  ];

  constructor(
    private router: Router,
    private sharedService: SharedService,
    private menuController: MenuController,
    private serviceGuard: ServiceGuardService
  ) {}

  ngOnInit() {
    this.username = this.sharedService.getUsername();
  }

  irA(destino: string) {
    const rutasValidas = ['cuenta', 'perfil', 'asignaturas', 'menu', 'home'];

    if (rutasValidas.includes(destino)) {
      this.router.navigate([destino]);
    } 
  }

  cerrarMenu() {
    this.menuController.close();
  }

  onLogout() {
    this.serviceGuard.setOutLogin (); 
    this.sharedService.setUsername(''); 
    this.menuController.close(); 
    this.router.navigate(['home']);
  }
}
<ion-header [translucent]="true">
  <ion-toolbar id="username">
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
    </ion-buttons>
    <ion-label>Bienvenido {{username}}</ion-label>
  </ion-toolbar>
</ion-header>

<ion-menu side="start" contentId="main-content">
  <ion-header>
    <ion-toolbar>
      <ion-icon
        (click)="cerrarMenu()"
        slot="start"
        name="arrow-back"
        id="arrow-back"
      ></ion-icon>
      <ion-label>Menu</ion-label>
    </ion-toolbar>
  </ion-header>

  <ion-content>
    <ion-list>
      <ion-menu-toggle *ngFor="let item of menuItems">
        <ion-item (click)="irA(item.destination)">
          <ion-icon slot="start" [name]="item.icon"></ion-icon>
          <ion-label (click)="item.label === 'Cerrar Sesión' ? onLogout() : null">{{ item.label }}</ion-label>
        </ion-item>
      </ion-menu-toggle>
    </ion-list>
  </ion-content>
</ion-menu>
<ion-router-outlet id="main-content"></ion-router-outlet>
